INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (1, N'1984', N'Джордж Оруэлл', N'Neoclassic', 2013, 4, N'Своеобразный антипод второй великой антиутопии XX века — "О дивный новый мир" Олдоса Хаксли. Что, в сущности, страшнее: доведенное до абсурда "общество потребления" — или доведенное до абсолюта "общество идеи"? По Оруэллу, нет и не может быть ничего ужаснее тотальной несвободы.', N'Зарубежная проза')
INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (2, N'О дивный новый мир', N'Олдос Хаксли', N'АСТ Москва', 2017, 5, N'"О дивный новый мир" - изысканная и остроумная антиутопия о генетически программируемом обществе потребления, в котором разворачивается трагическая история Дикаря - "Гамлета" этого мира.', N'Зарубежная проза')
INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (3, N'Удушье', N'Чак Паланик', N'АСТ', 2006, 8, N'Книга о молодом мошеннике, который каждодневно разыгрывает в дорогих ресторанах приступы удушья - и зарабатывает на этом неплохие деньги. Книга о сексоголиках, алкоголиках и шмоткаголиках. О любви, дружбе и философии. Cам Паланик говорит о ней: "Собираетесь прочесть? Зря!', N'Зарубежная проза')
INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (4, N'Три товарища', N'Эрих Мария Ремарк', N'Neoclassic', 2011, 6, N'"Три товарища" Эриха Марии Ремарка - самый красивый в нашем столетии роман о любви... Самый увлекательный в нашем столетии роман о дружбе. Самый трагический и пронзительный роман о человеческих отношениях за всю историю XX века.', N'Зарубежная проза')
INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (5, N'Отцы и дети', N'Иван Сергеевич Тургенев', N'Астрель', 2004, 7, N'Особенностью общественной жизни 60-х годов XIX века являлась идеологическая борьба двух поколений. Назревает конфликт между молодостью, жаждущей перемен, и консерватизмом зрелого возраста. Вечная тема отцов и детей, поднятая в одном из самых сильных романов И.С. Тургенева, актуальна и в наше время.', N'Российская проза')
INSERT INTO [dbo].[Books] ([BookID], [Name], [Author], [Publishing], [Year], [Rating], [Description], [Category]) VALUES (6, N'Мастер и Маргарита', N'Михаил Афанасьевич Булгаков', N'Азбука', 2012, 6, N'Мастер и Маргарита" М.А.Булгакова - самое удивительное и загадочное произведение XX века. Опубликованный в середине 1960-х, этот роман поразил читателей необычностью замысла, красочностью и фантастичностью действия, объединяющего героев разных эпох и культур. Автор создал "роман в романе", где сплетены воедино религиозно-историческая мистерия, московская "буффонада" и сверхъестественные сцены.', N'Российская фантастика')
